-- State Table courtesy Statetable.com

CREATE TABLE State (id integer, name varchar(255), abbreviation varchar(255), country varchar(255), type varchar(255), sort integer, status varchar(255), occupied varchar(255), notes varchar(255), fips_State varchar(255), assoc_press varchar(255), standard_federal_region varchar(255), census_region varchar(255), census_region_name varchar(255), census_division varchar(255), census_division_name varchar(255), circuit_court varchar(255));
